import React from 'react';
import io from "socket.io-client";
import { Button, Jumbotron, Container, Form, Col, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './HomePage.css';


function CreateForm(props){
    function handleClick(){
        const socket = io(this.state.endpoint);
        const stream = ss.createStream();

        // Send data
        socket.emit("demorequest");

        // Receive data
        /*
        ss(socket).on("demofile", (stream, filename) => {
        console.log(stream);
        });
        */

        socket.on("demofile", file=>{
            console.log(file);
        })
        console.log("create");
    };

    return(
        <Form id = "s">
            <Form.Row>
                <Col>   
                    <Form.File id="custom-file" label="Choose Demo to Upload..." custom/>
                </Col>
                <Col>   
                    <Button variant="create" onClick= {this.props.onClickCreate}>Create Session</Button>
                </Col>
                
            </Form.Row>
        </Form>
    );
}




function HomePage(props){

    return(
        <Container>
            <Jumbotron id = "createSessionSection">
                <CreateForm onClick = {props.onClickCreate}
                />
            </Jumbotron>
            <Jumbotron id = "joinSessionSection">
                <Form>
                    <Form.Row className="align-items-center">
                        <Col>
                            <Form.Control type="join" placeholder="Enter Join Code" />
                        </Col>
                        <Col>
                            <Button variant="join" onClick= {props.onClickJoin}>Join Session</Button>
                        </Col>
                    </Form.Row>
                </Form>
            </Jumbotron>
    
        
        </Container>
    )
}

export default HomePage;
