import React, {useEffect, useRef, useState} from 'react';
import { Button, Jumbotron, Container, Col, Row, Pagination} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';
import json from './123.json';



function PlaybackArea(props){
    const drawDots = props.dotPositions.map(position=>{
        return <div class ="dot" style = {{"left": position.x, "bottom": position.y}}/>
    });
        
    return(
        <div id="map">
            {drawDots}
        </div>
    );
}

function ControlBar(props){
    const [active, setActive] = useState(1);
    let items = [];

    function handleClick(key){
        setActive(key);
        props.onClick(key);

        console.log(key);
    }

    // Displaying pagination numbers
    for (let number = 1; number <= props.maxRounds; number++) {
        items.push(
          <Pagination.Item key={number} active={number === active} value = {number} onClick={e=>handleClick(e.target.value)}>
            {number}
          </Pagination.Item>,
        );
      }

    return(
        <div>
          <Pagination>{items}</Pagination>
        </div>
    );
}


function Game(props){
    const [hist, setHist] = useState({t:0, dotPositions:[]});
    const [currentRound, setCurrentRound] = useState(1);

    // For position calibration
    const x0 = 535, y0 = -527;   
    const xg0 = -520, yg0 = -984;
    const k = 0.2;

    useInterval(()=>{
        if(props.game != null && hist.t < props.game.rounds[currentRound].player_positions.length){
            let players = props.game.rounds[currentRound].player_positions[hist.t].players;

            let newHist = {
                t: hist.t + 1,
                dotPositions: []
            }
            
            for(const i in players){
                if(players[i].position != null){     // checks if player is still alive
                    let xg = players[i].position.x;
                    let yg = players[i].position.y;
                    let x = x0 + (xg-xg0)*k;
                    let y = y0 + (yg-yg0)*k;

                    newHist.dotPositions.push({x,y});
                }
            }

            setHist(newHist);
        }
        console.log("tick");   
    },5);

    function onClick(i){
        //setCurrentRound(i);
        //alert("current round: " + i)
    }

    return(
        <Jumbotron>
            <PlaybackArea dotPositions= {hist.dotPositions}/>
            <ControlBar maxRounds = {Object.keys(props.game.rounds).length} onClick = {onClick}/>
        </Jumbotron>
    );
   
}

function WatchPage(props){
    return(
        <Container>
            <Game game={json}/>
        </Container>
    )
}

//~~~~~~~~~~~~~~~~~

function useInterval(callback, delay) {
    const savedCallback = useRef();
  
    // Remember the latest callback.
    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);
  
    // Set up the interval.
    useEffect(() => {
      function tick() {
        savedCallback.current();
      }
      if (delay !== null) {
        let id = setInterval(tick, delay);
        return () => clearInterval(id);
      }
    }, [delay]);
  }

export default WatchPage;
