import React, {useState} from 'react';
import io from "socket.io-client";
import { Button, Jumbotron, Container, Form, Col, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import bsCustomFileInput from 'bs-custom-file-input'
import './HomePage.css';
import {Route, useHistory} from 'react-router-dom';
import ss from "socket.io-stream";


function CreateForm(props){
    const history = useHistory();
    const [fileName, setFileName] = useState("Choose Demo to Upload...");
    const [fileStatus, setFileStatus] = useState("No File Uploaded");

    function handleClick(){
        const socket = io(props.endpoint);

        // Sending to server
        socket.emit("create");
        
        history.push("/watch");

        {
        //const stream = ss.createStream();

        // Send data
        //socket.emit("demorequest");

        // Receive data
        /*
        ss(socket).on("demofile", (stream, filename) => {
        console.log(stream);
        });
        */

        /*
        socket.on("demofile", file=>{
            console.log(file);
        })  */
        }
    };

    function handleFile(e){
        const file = e.target.files[0];

        if(!file){
            setFileStatus("No file selected.");
            return;
        }

        if(!file.name.match(/\.(dem)$/)){
            setFileStatus("Invalid file format.");
            return;
        }

        if(file.size > 419430400){
            setFileStatus("File size too big.");
            return;
        }

        alert(fileStatus);
        setFileName(file.name);        // Set filename in textbox
    }

    return(
        <Form>
            <Form.Row>
                <Col>   
                    <Form.File 
                        id="custom-file" 
                        onChange={handleFile} 
                        label={fileName}
                        accept=".dem"
                        custom>
                    </Form.File>
                </Col>
                <Col>   
                    <Button variant="create" onClick= {() => handleClick()}>Create Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function JoinForm(props){
    const history = useHistory();

    function handleClick(){
        const socket = io(props.endpoint);

        socket.emit("join");

        history.push("/watch");
    }

    return(
        <Form>
            <Form.Row className="align-items-center">
                <Col>
                    <Form.Control type="join" placeholder="Enter Join Code" />
                </Col>
                <Col>
                    <Button variant="join" onClick= {() => handleClick()}>Join Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function HomePage(props){

    return(
        <Container>
            <Jumbotron id = "createSessionSection">
                <CreateForm endpoint={props.endpoint}/>
            </Jumbotron>
            <Jumbotron id = "joinSessionSection">
                <JoinForm endpoint = {props.endpoint}/>
            </Jumbotron>    
        </Container>
    )
}

export default HomePage;
