                history.push("/watch");
            }
        })
    }

    return(
        <Form onSubmit = {handleSubmit}>
            <Form.Row className="align-items-center">
                <Col>
                    <Form.Control type="text" placeholder="Enter Join Code" value = {textValue} onChange = {handleChange}/>
                </Col>
                <Col>
                    <Button variant="join" type = "submit">Join Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function HomePage(props){

    return(
        <Container>
            <Jumbotron id = "createSessionSection" >
                <CreateForm endpoint={props.endpoint} createRoomCallback = {props.createRoomCallback}/>
            </Jumbotron>
            <Jumbotron id = "joinSessionSection" >
                <JoinForm endpoint = {props.endpoint} gameCallback = {props.gameCallback}/>
            </Jumbotron>    
        </Container>
    )
}

export default HomePage;
