import React, {useEffect, useRef} from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';
import json from './123.json';



class PlaybackArea extends React.Component{
    constructor(props){
        super();
    }

    render(){
        const drawDots = this.props.dotPositions.map(position=>{
            return <div class ="dot" style = {{"left": position.x, "bottom": position.y}}/>
        });
        
        return(
            <div>
                <div id="map">
                    {drawDots}
                </div>
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();

        this.state={
            hist: {
                t: 0,
                dotPositions: []
            }
        }
    }

    componentDidMount(){
        const x0 = 535, y0 = -527;
        const xg0 = -520, yg0 = -984;
        const k = 0.2;

        this.interval = setInterval(()=>{
            if(this.props.game != null && this.state.hist.t < this.props.game.rounds[21].player_positions.length){
                let players = this.props.game.rounds[5].player_positions[this.state.hist.t].players;

                let newHist = {
                    t: this.state.hist.t + 1,
                    dotPositions: []
                }
                
                for(const i in players){
                    if(players[i].position != null){     // checks if player is still alive
                        let xg = players[i].position.x;
                        let yg = players[i].position.y;
                        let x = x0 + (xg-xg0)*k;
                        let y = y0 + (yg-yg0)*k;

                        newHist.dotPositions.push({x,y});
                    }
                }

                this.setState({hist: newHist});
            }
            console.log("tick");   
        },15);
    }

    componentWillUnmount(){
        clearInterval(this.interal);
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea dotPositions= {this.state.hist.dotPositions}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={json}/>
        </Container>
    )
}

export default WatchPage;
