import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';


class PlaybackArea extends React.Component{
    constructor(props){
        super();
    }

    render(){
        const x0 = 919, y0 = -622;
        const xg0 = 1384, yg0 = -1480;
        const k = 0.2

        let xg = 1296;//this.props.game == null ? 0 : this.props.game.rounds[1].player_positions[85].position_points[0].position.x;
        let yg = -160//this.props.game == null ? 0 : this.props.game.rounds[1].player_positions[85].position_points[0].position.y;
        let x = x0 + (xg-xg0)*k;
        let y = y0 + (yg-yg0)*k;

        let style = {
            "left": x,
            "bottom": y
        }

        let allDots = [<div class ="dot" style = {{"left": 919, "bottom": -622}}/>, <div class ="dot" style = {{"left": 1000, "bottom": -600}}/>];

        return(
            <div>
                <div id="map">
                    {allDots};
                </div>
                
                
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
