import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';


class PlaybackArea extends React.Component{
    constructor(props){
        super();
    }

    render(){
        let allDots = [];
        /*
        const x0 = 919, y0 = -622;
        const xg0 = -520, yg0 = -984;
        const k = 0.19;
       


        if(this.props.game != null){
            let player_pos = this.props.game.rounds[1].player_positions;

            for(let i in player_pos){
                let xg = player_pos[i].position_points[0].position.x;
                let yg = player_pos[i].position_points[0].position.y;
                let x = x0 + (xg-xg0)*k;
                let y = y0 + (yg-yg0)*k;

                allDots.push(<div class ="dot" style = {{"left": x, "bottom": y}}/>)
            }
        }
*/
        allDots.push(<div class ="dot" style = {{"left": 530, "bottom": -530}}/>)

        return(
            <div>
                <div id="map">
                    {allDots};
                </div>
                
                
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
