import React, {Component} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import io from "socket.io-client";
import ss from "socket.io-stream";
import HomePage from "./pages/HomePage";

class App extends Component {

  constructor(){
    super();
    this.state = {
      "endpoint": "localhost:5000"
    };
  }

  componentDidMount(){
    const socket = io(this.state.endpoint);
  }

  handleClick(){
    const socket = io(this.state.endpoint);
    const stream = ss.createStream();

    // Send data
    socket.emit("demorequest");

    // Receive data

    /*
    ss(socket).on("demofile", (stream, filename) => {
      console.log(stream);
    });
    */

    socket.on("demofile", file=>{
      console.log(file);
    })
  }

  render(){
    return(
      <Router>
        <div className="App">
          <Link to="/">Home</Link>
        </div>

        <Switch>
          <Route path="/"><HomePage onClick={()=>this.handleClick()}/></Route>
          <Route path="/watch"><WatchRouter /></Route>
        </Switch>
      </Router>
    )
  }
}


export default App;
