import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';
import { CMsgGCCStrike15_v2_MatchmakingGC2ClientHello } from 'demofile/dist/protobufs/cstrike15_usermessages';


class PlaybackArea extends React.Component{
    constructor(props){
        super();
        this.state={
            hist: {
                t: 0,
                dotPositions: []
            }
        }
    }

    


    render(){
        const x0 = 535, y0 = -527;
        const xg0 = -520, yg0 = -984;
        const k = 0.2;

        let drawDots = () =>{
            this.state.hist.dotPositions.map(position=>{
                return <div class ="dot" style = {{"left": position.x, "bottom": position.y}}/>
            });
        }

        setInterval(()=>{
            if(this.props.game != null){
                let playerPos = this.props.game.rounds[5].player_positions;
                
                for(let i in playerPos){
                    if(this.state.hist.t < playerPos[i].position_points.length){
                        const currentHist = this.state.hist;

                        let xg = playerPos[i].position_points[currentHist.t].position.x;
                        let yg = playerPos[i].position_points[currentHist.t].position.y;
                        let x = x0 + (xg-xg0)*k;
                        let y = y0 + (yg-yg0)*k;

                        this.setState({
                            hist:{
                                t: currentHist.t + 1,
                                dotPositions: currentHist.dotPositions.concat({x, y})
                            }
                        });

                        console.log(x,y);

                    }else{
                        return alert("Finished Round");
                    }
                }
                
            }    
        },10000);

        
        return(
            <div>
                <div id="map">
                    {drawDots()}
                </div>
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();

    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
