import React, {Component} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import io from "socket.io-client";
import ss from "socket.io-stream";
import HomePage from "./pages/HomePage";
import WatchPage from "./pages/WatchPage";

class App extends Component {

  constructor(){
    super();
    this.state = {
      "endpoint": "localhost:5000"
    };
  }

  componentDidMount(){
    const socket = io(this.state.endpoint);
  }

  handleClickCreate(){
    const socket = io(this.state.endpoint);
    const stream = ss.createStream();

    // Send data
    socket.emit("demorequest");

    // Receive data

    /*
    ss(socket).on("demofile", (stream, filename) => {
      console.log(stream);
    });
    */

    socket.on("demofile", file=>{
      console.log(file);
    })

    console.log("create");
  }

  handleClickJoin(){
    console.log("join");
  }

  render(){
    return(
      <Router>
        <div className="App">
          <Link to="/">Home</Link>
        </div>

        <Switch>
          <Route path="/"><HomePage onClickCreate={()=>this.handleClickCreate()} onClickJoin={()=>this.handleClickJoin()}/></Route>
          <Route path="/watch"><WatchPage/></Route>
        </Switch>
      </Router>
    )
  }
}


export default App;
