import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';

class PlaybackArea extends React.Component{
    constructor(props){
        super();
    }

    PlayerDots(){
        

        let x = this.props.game.round[0].player_positions[85].position_point[0].position.x;
        let y = this.props.game.round[0].player_positions[85].position_point[0].position.x;

        let allDots = <div class = "dot" style = {`left:${x}`} />;

        return(
            allDots
        )
    }

    render(){
        return(
            <div>
                <div id="map">
                    <PlayerDots/>
                </div>
                
                
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
