import React, {useState} from 'react';
import io from "socket.io-client";
import { Button, Jumbotron, Container, Form, Col, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import bsCustomFileInput from 'bs-custom-file-input'
import './HomePage.css';
import {Route, useHistory} from 'react-router-dom';
import ss from "socket.io-stream";
import axios from "axios";


function CreateForm(props){
    const history = useHistory();
    const [file, setFile] = useState('');
    const [fileName, setFileName] = useState("Choose Demo to Upload...");
    const [fileStatus, setFileStatus] = useState(0);
    const [fileStatusName, setFileStatusName] = useState("");

    function handleClick(){
        //const socket = io(props.endpoint);

        // Sending to server
        //socket.emit("create");
        
        //history.push("/watch");

        {
        //const stream = ss.createStream();

        // Send data
        //socket.emit("demorequest");

        // Receive data
        /*
        ss(socket).on("demofile", (stream, filename) => {
        console.log(stream);
        });
        */

        /*
        socket.on("demofile", file=>{
            console.log(file);
        })  */
        }
    };

    function handleFile(e){
        const file = e.target.files[0];

        setFileStatus(1);
        setFileName(file.name);
        setFile(file);

        {/*
        if(!file){
            setFileStatusName("No file selected");
            setFileStatus(-1);
            return;
        }

        else if(!file.name.match(/\.(dem)$/)){
            setFileStatusName("Invalid file format");
            setFileStatus(-1);
            setFileName(file.name);
            return;
        }

        else if(file.size > 419430400){
            setFileStatusName("File size too big");
            setFileStatus(-1);
            setFileName(file.name);
            return;
        }
        else{
            setFileStatus(1);
            setFileName(file.name);
            setFile(file);
        }
    */}
    }

    async function onSubmit(e){
        e.preventDefault();

        console.log(file);
        
        const formData = new FormData();
        console.log(formData);
        formData.append("file", file);

        console.log(formData);

        try{
            const res = await axios.post("/upload", formData,{
                headers:{
                    "Content-Type": "multipart/form-data"
                } 
            });

            const {filename, filepath} = res.data;

        }catch(err){
            if(err.response.status === 500){
                console.log("Problem with server");
            } else{
                console.log(err.response.data.msg)
            }
        }
        
    
    }

    return(
        <Form onSubmit={onSubmit}>
            <Form.Row>
                <Col>
                    <Form.File id="formcheck-api-custom" custom> 
                        <Form.File.Input isValid = {fileStatus > 0} isInvalid = {fileStatus < 0} onChange ={handleFile}/>
                        <Form.File.Label data-browse="Browse">
                            {fileName}
                        </Form.File.Label>
                        <Form.Control.Feedback type="invalid">{fileStatusName}</Form.Control.Feedback>
                    </Form.File>
                    
                </Col>
                <Col>   
                    <Button variant="create" type = "submit" >Create Session</Button>
                    {/*onClick= {() => handleClick()}*/}
                </Col>
            </Form.Row>
        </Form>
    );
}

function JoinForm(props){
    const history = useHistory();

    function handleClick(){
        const socket = io(props.endpoint);

        socket.emit("join");

        history.push("/watch");
    }

    return(
        <Form>
            <Form.Row className="align-items-center">
                <Col>
                    <Form.Control type="join" placeholder="Enter Join Code" />
                </Col>
                <Col>
                    <Button variant="join" onClick= {() => handleClick()}>Join Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function HomePage(props){

    return(
        <Container>
            <Jumbotron id = "createSessionSection">
                <CreateForm endpoint={props.endpoint}/>
            </Jumbotron>
            <Jumbotron id = "joinSessionSection">
                <JoinForm endpoint = {props.endpoint}/>
            </Jumbotron>    
        </Container>
    )
}

export default HomePage;
