import React, {Component} from 'react';
import {BrowserRouter as Router, Route} from "react-router-dom";
import './App.css';
import HomePage from "./pages/HomePage";
import WatchPage from "./pages/WatchPage";

class App extends Component {

  constructor(){
    super();
    this.state = {
      "endpoint": "localhost:5000"
    };
  }

  getGame(gameFile){
    this.setState({game: gameFile})
  }

  render(){
    return(
      <Router>
          <switch>
            <Route exact path="/" render={()=><HomePage endpoint={this.state.endpoint} gameCallBack={this.getGame}/>}/>
            <Route path="/watch" render={()=><WatchPage/>}></Route>
          </switch>
      </Router>
    )
  }
}


export default App;
