            let players = props.game.rounds[currentRound].player_positions[playbackState.t].players;

            let newState = {
                t: playbackState.t + 1,
                dotPositions: []
            }
            
            for(const i in players){
                if(players[i].position != null){     // checks if player is still alive
                    let xg = players[i].position.x;
                    let yg = players[i].position.y;
                    let x = x0 + (xg-xg0)*k;
                    let y = y0 + (yg-yg0)*k;

                    newState.dotPositions.push({x,y});
                }
            }

            setPlaybackState(newState);
        }
        console.log("tick");   
    },5);

    function handleClick(i){
        setCurrentRound(i);
        setPlaybackState({t:0, dotPositions:[]});
    }

    return(
        <Jumbotron>
            <PlaybackArea dotPositions= {playbackState.dotPositions}/>
            <ControlBar maxRounds = {Object.keys(props.game.rounds).length} onClick = {handleClick}/>
        </Jumbotron>
    );
    */
   return <p></p>
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game} endpoint={props.endpoint}/>
        </Container>
    )
}

//~~~~~~~~~~~~~~~~~

function useInterval(callback, delay) {
    const savedCallback = useRef();
  
    // Remember the latest callback.
    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);
  
    // Set up the interval.
    useEffect(() => {
      function tick() {
        savedCallback.current();
      }
      if (delay !== null) {
        let id = setInterval(tick, delay);
        return () => clearInterval(id);
      }
    }, [delay]);
  }

export default WatchPage;
