import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';


class PlaybackArea extends React.Component{
    constructor(props){
        super();
    }

    render(){
        let x0 = 39, y0 = 27;
        let x = this.props.game == null ? 0 : this.props.round[0].player_positions[85].position_point[0].position.x;
        let y = this.props.game == null ? 0 : this.props.round[0].player_positions[85].position_point[0].position.y;

        let style = {
            "left": x0,
            "top": y0
        }

        return(
            <div>
                <div id="map">
                <div class = "dot" style = {style} />;
                </div>
                
                
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
