import React from 'react';
import io from "socket.io-client";
import { Button, Jumbotron, Container, Form, Col, Row} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './HomePage.css';
import {Route, useHistory} from 'react-router-dom';
import ss from "socket.io-stream";


function CreateForm(props){
    const history = useHistory();

    function handleClick(){
        const socket = io(props.endpoint);

        // Sending to server
        socket.emit("create");
        
        history.push("/watch");

        {
        //const stream = ss.createStream();

        // Send data
        //socket.emit("demorequest");

        // Receive data
        /*
        ss(socket).on("demofile", (stream, filename) => {
        console.log(stream);
        });
        */

        /*
        socket.on("demofile", file=>{
            console.log(file);
        })  */
        }
    };

    function handleChange(e){
        console.log("upload");
    }

    return(
        <Form>
            <Form.Row>
                <Col>   
                    <Form.File id="custom-file" onChange={(e)=>handleChange()} label="Choose Demo to Upload..." custom/>
                </Col>
                <Col>   
                    <Button variant="create" onClick= {() => handleClick()}>Create Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function JoinForm(props){
    const history = useHistory();

    function handleClick(){
        const socket = io(props.endpoint);

        socket.emit("join");

        history.push("/watch");
    }

    return(
        <Form>
            <Form.Row className="align-items-center">
                <Col>
                    <Form.Control type="join" placeholder="Enter Join Code" />
                </Col>
                <Col>
                    <Button variant="join" onClick= {() => handleClick()}>Join Session</Button>
                </Col>
            </Form.Row>
        </Form>
    );
}

function HomePage(props){

    return(
        <Container>
            <Jumbotron id = "createSessionSection">
                <CreateForm endpoint={props.endpoint}/>
            </Jumbotron>
            <Jumbotron id = "joinSessionSection">
                <JoinForm endpoint = {props.endpoint}/>
            </Jumbotron>    
        </Container>
    )
}

export default HomePage;
