import React from 'react';
import { Button, Jumbotron, Container, Col, Row, Image} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './WatchPage.css';


class PlaybackArea extends React.Component{
    constructor(props){
        super();
        this.state={
            dot : {
                x:0, y:0
            }
        }
    }

    componentDidMount(){
    }


    render(){
        const x0 = 535, y0 = -527;
        const xg0 = -520, yg0 = -984;
        const k = 0.2;
        let tp = 0;

        setInterval(()=>{
            if(this.props.game != null){
                let playerPos = this.props.game.rounds[5].player_positions;
                
                
                for(let i in playerPos){
                    if(tp < playerPos[i].position_points.length){
                        let xg = playerPos[i].position_points[tp].position.x;
                        let yg = playerPos[i].position_points[tp].position.y;
                        let x = x0 + (xg-xg0)*k;
                        let y = y0 + (yg-yg0)*k;

                        this.setState({dot:{x:x,y:y}});
                    }else{
                        return alert("Finished Round");
                    }
                }

                tp++;
            }    
        },1000);

        <div class ="dot" style = {{"left": x, "bottom": y}}/>

        this.setState({dots: this.dots.concat()})
       
        return(
            <div>
                <div id="map">
                    <div class ="dot" style = {{"left": this.state.dot.x, "bottom": this.state.dot.y}}/>
                </div>
                
                
            </div>
        );
    }
}

class Game extends React.Component{
    constructor(props){
        super();
    }

    render(){
        return(
            <Jumbotron>
                <PlaybackArea game={this.props.game}/>
            </Jumbotron>
        );
    }
}

function WatchPage(props){
    return(
        <Container>
            <Game game={props.game}/>
        </Container>
    )
}

export default WatchPage;
